// http://www.devsumo.com/technotes/2014/06/tinymce-4-rolling-up-toolbar-buttons/
tinymce.PluginManager.add('rollups', function(editor, url) {
    editor.addMenuItem('savepage', {text: gettext('Save'), icon: 'save', cmd: 'mceSave'});
    editor.addButton('editrollup', {type   : 'MenuButton', text: gettext('Edit'), title : gettext('Edit'),
                    menu   : [editor.menuItems['undo'],
                        editor.menuItems['redo'],
                        editor.menuItems['cut'],
                        editor.menuItems['copy'],
                        editor.menuItems['paste'],
                        editor.menuItems['selectall'],] });
    editor.addButton('fontrollup', {type   : 'MenuButton', text: gettext('Font Style'), title : gettext('Style'),
                    menu   : [editor.menuItems['charmap'], 
                        editor.menuItems['bold'],
                        editor.menuItems['italic'],
                        editor.menuItems['strikethrough'],
                        editor.menuItems['underline'],
                        editor.menuItems['superscript'],
                        editor.menuItems['subscript'],] });

});